import React, { useState } from 'react';
import './skills.scss';
import AcUnitIcon from '@mui/icons-material/AcUnit';
import BuildCircleIcon from '@mui/icons-material/BuildCircle';
import ComputerIcon from '@mui/icons-material/Computer';
import AndroidIcon from '@mui/icons-material/Android';

const Skills = () => {
    const [expanded, setExpanded] = useState(false);
    const [cardContent, setCardContent] = useState('');
    const [isCardVisible, setIsCardVisible] = useState(false); // New state to track card visibility

    const handleSkillClick = () => {
        setExpanded(!expanded);
    };

    const handleFrontend = () => {
        setCardContent('Front-end Skills: HTML, CSS, JavaScript, React, etc.');
        setIsCardVisible(true); // Show card content when a division is clicked
    };

    const handleBackend = () => {
        setCardContent('Back-end Skills: Node.js, Express, MongoDB, etc.');
        setIsCardVisible(true); // Show card content when a division is clicked
    };

    const handleSoftSkills = () => {
        setCardContent('Soft Skills: Communication, Teamwork, Problem-solving, etc.');
        setIsCardVisible(true); // Show card content when a division is clicked
    };

    const handleAI = () => {
        setCardContent('AI & Other Skills: Machine Learning, Python, TensorFlow, etc.');
        setIsCardVisible(true); // Show card content when a division is clicked
    };

    const handleDivisionDoubleClick = () => {
        setIsCardVisible(false); // Hide card content when a division is double-clicked
    };

    return (
        <div className='skills'>
            <div className="center">
                <div className={`skill-circle ${expanded ? 'expanded' : ''}`} onClick={handleSkillClick}>
                    SKILLS
                    {expanded && (
                        <div className="divisions">
                            <div className="division" onClick={handleFrontend} onDoubleClick={handleDivisionDoubleClick}>
                                <div><ComputerIcon /></div>
                                <div>
                                    <p className='fs'>Front-end Skills</p>
                                </div>
                            </div>
                            <div className="division" onClick={handleBackend} onDoubleClick={handleDivisionDoubleClick}>
                                <div><BuildCircleIcon /></div>
                                <p className='bs'>Back-end Skills</p>
                            </div>
                            <div className="division" onClick={handleSoftSkills} onDoubleClick={handleDivisionDoubleClick}>
                                <div><AcUnitIcon /></div>
                                <p className='ss'>Soft Skills</p>
                            </div>
                            <div className="division" onClick={handleAI} onDoubleClick={handleDivisionDoubleClick}>
                                <div><AndroidIcon /></div>
                                <p className='as'>AI & Other Skills</p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            {isCardVisible && expanded && ( // Render card only if isCardVisible is true
                <div className='card'>
                    <p>{cardContent}</p>
                </div>
            )}
        </div>
    );
}

export default Skills;
