import React, { useState } from 'react';
import './skills.scss';
import AcUnitIcon from '@mui/icons-material/AcUnit';
import BuildCircleIcon from '@mui/icons-material/BuildCircle';
import ComputerIcon from '@mui/icons-material/Computer';
import AndroidIcon from '@mui/icons-material/Android';

const Skills = () => {
    const [expanded, setExpanded] = useState(false);
    const [isHovered, setIsHovered] = useState(false);

    const handleSkillClick = () => {
        setExpanded(!expanded);
    };

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    return (
        <div className='skills'>
            <div className="center">
                <div className={`skill-circle ${expanded ? 'expanded' : ''}`} onClick={handleSkillClick}>
                    SKILLS
                    {expanded && (
                        <div className="divisions">
                            <div className="division" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                                <div><ComputerIcon /></div>
                                <div>
                                    <p className='fs'>Front-end Skills</p>
                                    {isHovered && (
                                        <div className="hover">
                                            <p>Front-end skills include HTML, CSS, JavaScript, and React.</p>
                                        </div>
                                    )}
                                </div>
                            </div>
                            <div className="division">
                                <div><BuildCircleIcon /></div>
                                <p className='bs'>Back-end Skills</p>
                            </div>
                            <div className="division">
                                <div><AcUnitIcon /></div>
                                <p className='ss'>Soft Skills</p>
                            </div>
                            <div className="division">
                                <div><AndroidIcon /></div>
                                <p className='as'>AI & Other Skills</p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Skills;
