import React, { useState } from 'react';
import './skills.scss';

const Skills = () => {
  const [expanded, setExpanded] = useState(false);

  const handleSkillClick = () => {
    setExpanded(!expanded);
  };

  return (
    <div className='skills'>
      <div className="center">
        <div className={`skill-circle ${expanded ? 'expanded' : ''}`} onClick={handleSkillClick}>
          SKILLS
          {expanded && (
            <div className="divisions">
              <div className="division">Text 1</div>
              <div className="division">Text 2</div>
              <div className="division">Text 3</div>
              <div className="division">Text 4</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Skills;
