import React, { useState } from 'react';
import './skills.scss';
import AcUnitIcon from '@mui/icons-material/AcUnit';
import BuildCircleIcon from '@mui/icons-material/BuildCircle';
import ComputerIcon from '@mui/icons-material/Computer';
import AndroidIcon from '@mui/icons-material/Android';

const Skills = () => {
    const [expanded, setExpanded] = useState(false);
    const [cardContent, setCardContent] = useState('');

    const handleSkillClick = () => {
        setExpanded(!expanded);
    };

    const handleFrontend = () => {
        setCardContent('Front-end Skills: HTML, CSS, JavaScript, React, etc.');
    };

    const handleBackend = () => {
        setCardContent('Back-end Skills: Node.js, Express, MongoDB, etc.');
    };

    const handleSoftSkills = () => {
        setCardContent('Soft Skills: Communication, Teamwork, Problem-solving, etc.');
    };

    const handleAI = () => {
        setCardContent('AI & Other Skills: Machine Learning, Python, TensorFlow, etc.');
    };

    return (
        <div className='skills'>
            <div className="center">
                <div className={`skill-circle ${expanded ? 'expanded' : ''}`} onClick={handleSkillClick}>
                    SKILLS
                    {expanded && (
                        <div className="divisions">
                            <div className="division" onClick={handleFrontend}>
                                <div><ComputerIcon /></div>
                                <div>
                                    <p className='fs'>Front-end Skills</p>
                                </div>
                            </div>
                            <div className="division" onClick={handleBackend}>
                                <div><BuildCircleIcon /></div>
                                <p className='bs'>Back-end Skills</p>
                            </div>
                            <div className="division" onClick={handleSoftSkills}>
                                <div><AcUnitIcon /></div>
                                <p className='ss'>Soft Skills</p>
                            </div>
                            <div className="division" onClick={handleAI}>
                                <div><AndroidIcon /></div>
                                <p className='as'>AI & Other Skills</p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
            {cardContent && (
                <div className='card'>
                    <p>{cardContent}</p>
                </div>
            )}
        </div>
    );
}

export default Skills;
